config.files:
{
    "serverconfig.txt": {
        "parser": "properties",
        "find": {
            "Note": " to change any value go to startup, and change there!",
            "Notas": " para alterar qualquer valor va para startup, e altere la!",
            "worldpath": "/home/container/saves/Worlds",
            "worldname": "{{server.build.env.WORLD_NAME}}",
            "world": "/home/container/saves/Worlds/{{server.build.env.WORLD_NAME}}.wld",
            "difficulty": "{{server.build.env.DIFFICULTY}}",
            "autocreate": "{{server.build.env.AUTO_CREATE}}",
            "port": "{{server.build.default.port}}",
            "maxplayers": "{{server.build.env.MAX_PLAYERS}}",
            "motd": "{{server.build.env.MOTD}}",
            "password": "{{server.build.env.SERVER_PASSWORD}}",
            "modpath": "/home/container/Mods",
            "secure": "{{server.build.env.SECURE}}"
        }
    },
    "Mods/enabled.json": {
        "parser": "json",
        "find": {
            "": ""
        }
    }
}
